$(document).ready(() => {
  $("#showFilterLeft").click(() => {
    $(".filterSearchIdx").toggle("slider");
    console.log($(".filterSearchIdx").css("width"));
    if (
      $(".filterSearchIdx").css("width") === "176.969px" ||
      $(".filterSearchIdx").css("width") === "176.984px" ||
      $(".filterSearchIdx").css("width") === "177px"
    ) {
      console.log("Đóng Menu Tăng FUll Width ");
      $(".search__content___flex____right").addClass("fullWidth");
    } else {
      console.log("Mở Menu Giảm  FUll Width ");
      $(".search__content___flex____right").removeClass("fullWidth");
    }
  });
  $("#closeFilterSearchIdx").click(() => {
    $(".filterSearchIdx").toggle("hide");
  });
});
