//Show Menu Search Area List User //
$(document).ready(() => {
  $("#showArea").click(() => {
    if ($("#listAreaSearch").css("display") === "none") {
      $("#listAreaSearch").show("sidler");
    }
    $(document).on("click", (event) => {
      var $trigger = $("#showArea");
      var $trigger2 = $("#listAreaSearch");
      if (
        $trigger !== event.target &&
        !$trigger.has(event.target).length &&
        $trigger2 !== event.target &&
        !$trigger2.has(event.target).length
      ) {
        $("#listAreaSearch").hide("sidler");
      } else {
        console.log("OK2");
      }
    });
  });
});
