//Show menu SliderBar Admin
if (widthWebIdxJq > 786) {
  let elementSpanMenuFx = $(".name__menuFxBar_w");
  $(document).ready(() => {
    $("#showMenuBar").click(() => {
      if ($(".menuFxBar").width() === 60) {
        $(".menuFxBar").width("230px");
        $("#page__topbar").css("left", "230px");
        $("#page__topbar").css("width", "calc(100% - 230px)");
        $("#footerAdmin").css("left", "230px");
        $("#footerAdmin").css("width", "calc(100% - 230px)");
        $("#menuDropdownAdmin").css("display", "none");
        $(".page__main").css("margin-left", "172px");

        for (let element of elementSpanMenuFx) {
          $("." + element.classList[1]).css("display", "block");
        }
      } else if ($(".menuFxBar").width() === 230) {
        $(".menuFxBar").width("60px");
        $("#page__topbar").css("width", "calc(100% - 60px)");
        $("#page__topbar").css("left", "60px");
        $("#menuDropdownAdmin").css("display", "");
        $("#footerAdmin").css("left", "60px");
        $("#footerAdmin").css("width", "calc(100% - 60px)");
        $(".page__main").css("margin-left", "0");
        for (let element of elementSpanMenuFx) {
          $("." + element.classList[1]).css("display", "none");
        }
      }
    });
  });
} else {
  $(document).ready(() => {
    $("#showMenuBar").click(() => {
      if ($(".menuFxBar").hasClass("slideInAdm")) {
        // $(".menuFxBar").toggleClass("slideOutAdm");
        $(".menuFxBar").toggleClass("slideOutAdm");
      } else {
        $(".menuFxBar").toggleClass("slideInAdm");
      }
      $(document).on("click", (event) => {
        var $trigger = $("#showMenuBar");
        var $trigger2 = $(".menuFxBar");
        if (
          $trigger !== event.target &&
          !$trigger.has(event.target).length &&
          $trigger2 !== event.target &&
          !$trigger2.has(event.target).length
        ) {
          if (!$(".menuFxBar").hasClass("slideOutAdm"))
            $(".menuFxBar").toggleClass("slideOutAdm");
        }
      });
    });
  });
}

