$(document).ready(() => {
  let count = 0;
  let product_item = $(".product__wp___item").length;
  let widthItemProduct = $("#productSliderShop").width() / 6;
  let widthAllItemProduct = widthItemProduct * product_item;
  let spacing = widthAllItemProduct - 6 * widthItemProduct;
  $("#sliderArrowRight").click(() => {
    count += widthItemProduct;
    if (count > spacing) count = 0;

    $("#productSliderShop").css("transform", `translateX(-${count}px)`);
  });
  $("#sliderArrowLeft").click(() => {
    count -= widthItemProduct;
    if (count >= 0) {
      $("#productSliderShop").css("transform", `translateX(-${count}px)`);
    } else {
      count = 0;
    }
  });
});
